import React from "react";
import { Link } from "react-router-dom";

const RestaurantCard = ({ restaurant, cuisineType }) => {
  return (
    <div className="restaurant-card">
      <h2>{restaurant.name}</h2>
      <p>{restaurant.description}</p>
      <p>Address: {restaurant.address}, {restaurant.city}, {restaurant.state}, {restaurant.zipcode}</p>
      <p>Price Range: {restaurant.price_range}</p>
      <p>Contact Phone: {restaurant.contact_phone_number}</p>
      <Link to={`/restaurant/${restaurant.id}`}>View Details</Link>
      <Link to={`/cuisine/${cuisineType.id}`}>View Cuisine Type</Link>
      <img
        src={cuisineType.restaurant_image}
        alt={cuisineType.type}
        style={{ width: "100px", height: "100px" }}
      />
    </div>
  );
};

export default RestaurantCard;
